CREATE TABLE "DIM_AZIENDA" ( "ID_AZIENDA" INTEGER (3) PRIMARY KEY ,"NOME_AZIENDA" VARCHAR (30)); 
CREATE TABLE "DIM_CONTRATTO" ( "ID_CONTRATTO" INTEGER (3) PRIMARY KEY ,"TIPO_CONTRATTO" VARCHAR (20)); 
CREATE TABLE "DIM_DATA" ( "ID_DATA" INTEGER (3) PRIMARY KEY ,"MESE" INTEGER (2),"ANNO" INTEGER (4)); 
CREATE TABLE "DIM_DIPARTIMENTO" ( "ID_DIPARTIMENTO" INTEGER (3) PRIMARY KEY ,"NOME" VARCHAR (20)); 
CREATE TABLE "DIM_FILIALE" ( "ID_FILIALE" INTEGER (3) PRIMARY KEY ,"SEDE" INTEGER (3)); 
CREATE TABLE "DIM_LIVELLO" ( "ID_LIVELLO" INTEGER (3) PRIMARY KEY ,"GRADO" INTEGER (3)); 
CREATE TABLE "DIM_LUOGO_NASCITA" ( "ID_LUOGO_NASCITA" INTEGER (3) PRIMARY KEY ,"DESCR_COM_NASC" VARCHAR (30),"PROVINCIA_NASC" CHAR (2)); 
CREATE TABLE "DIM_ANAGRAFICA" ( "MATRICOLA" INTEGER (4) PRIMARY KEY ,"COGNOME" CHAR (20),"NOME" CHAR (20),"CODICE_FISCALE" CHAR (16),"EMAIL" VARCHAR (40),"DATA_ASSUNZIONE" DATE ,"DATA_DIMISSIONE" DATE ,"DATA_NASCITA" DATE ,"NUMERO_TELEFONO" VARCHAR (12),"CELLULARE" VARCHAR (12),"RES_INDIRIZZO" VARCHAR (30),"RES_CAP" INTEGER (5),"RES_CITTA" VARCHAR (30),"RES_PROVINCIA" CHAR (2),"DOM_INDIRIZZO" VARCHAR (30),"DOM_CAP" INTEGER (5),"DOM_CITTA" VARCHAR (30),"DOM_PROVINCIA" CHAR (2),"ID_AZIENDA" INTEGER (3) FOREIGN KEY REFERENCES DIM_AZIENDA("ID_AZIENDA"),"ID_FILIALE" INTEGER (3) FOREIGN KEY REFERENCES DIM_FILIALE("ID_FILIALE"),"ID_DIPARTIMENTO" INTEGER (3) FOREIGN KEY REFERENCES DIM_DIPARTIMENTO("ID_DIPARTIMENTO"),"ID_CONTRATTO" INTEGER (3) FOREIGN KEY REFERENCES DIM_CONTRATTO("ID_CONTRATTO"),"ID_LUOGO_NASCITA" INTEGER (3) FOREIGN KEY REFERENCES DIM_LUOGO_NASCITA("ID_LUOGO_NASCITA")); 
CREATE TABLE "FACT_BUSTA_PAGA" ( "STIPENDIO_LORDO" FLOAT ,"COMPETENZE" FLOAT ,"STIPENDIO_NETTO" FLOAT ,"ID_DATA" INTEGER (3) FOREIGN KEY REFERENCES DIM_DATA("ID_DATA"),"ID_LIVELLO" INTEGER (3) FOREIGN KEY REFERENCES DIM_LIVELLO("ID_LIVELLO"),"MATRICOLA" INTEGER (4) FOREIGN KEY REFERENCES DIM_ANAGRAFICA("MATRICOLA")); 